import React from 'react';
import ReactTable from "react-table";
import 'react-table/react-table.css'
//import Post from './Post'

class PostTable extends React.Component{
  constructor(props)
  {
  	super(props);
  	this.state = {
  		isInEditable: false,
  		value: '',
  		category_id: ''
  	}
  	this.handleDeleteRecord = this.handleDeleteRecord.bind(this);
  	this.getCategory = this.getCategory.bind(this);
  	this.handleToggle = this.handleToggle.bind(this);
  }	

  handleDeleteRecord(id)
	{
	  var self = this;
	  $.ajax({
      method: 'DELETE',
      url: '/posts/' + id,
      success: function(data) {
        self.props.handleDeleteRecord(id);
      }
     });
	}

	handleToggle(id) {
    this.setState({ isInEditable: !this.state.isInEditable });
    this.setState({ value: "editable" });
    console.log(this.state.value);
    }

	getCategory(category_id)
	{
		let cname = this.props.categories.map((cat) => {return cat.id.toString() === category_id.toString() ? cat.name : ''});
		return cname;
	}

  render() {
  	const onChangeFct = () => console.log("onChange usually handled by redux");
  	//render: props => <input value={props.row.name} onChange={onChangeFct} />
  	const columns =[
         {
             Header: "Name",
             accessor: "name",
             style: {
                 textAlign: "right"
             },
             width: 100,
             maxwidth: 100,
             minwidth: 100,
             Cell: props => {
                return (
                	<div>
                	{ this.state.isIneditable &&
                	  <input name="name"
                 defaultValue={props.original.post.name}
                 className="form-control"
                 type="text"
                 id="name"
                  />
                	}

                	</div>

                )
              }

         },
         {
            Header: "Title",
            accessor: "title",
            sortable: false,
            filterable: false
        },
        {
            Header: "Author",
            accessor: "author",
            sortable: false,
            filterable: false
        },
        {
            Header: "Description",
            accessor: "description",
            sortable: false,
            filterable: false
        },
        {
            Header: "Category",
            accessor: "category_id",
            sortable: false,
            filterable: false,
            Cell: props => {
                return (
                  this.getCategory(props.original.category_id)
                )
               }
        },
        {
            Header: "Published at",
            accessor: "published_at",
            sortable: false,
            filterable: false
        },
        {
            Header: "Last edited at",
            accessor: "last_edited_at",
            sortable: false,
            filterable: false
        },
        {
            Header: "Action",
            Cell: props => {
                return (
                	<div>
                	<a className="btn btn-primary btn-xs"
                     onClick={() => {
                        this.handleToggle(props.original.id);

                    }}
                      >
                     Edit
                   </a>
                   <a className="btn btn-danger btn-xs"
                   onClick={() => {
                        this.handleDeleteRecord(props.original.id);
                    }}
                    >
                     Delete
                  </a>
                  </div>
                )
            },
            sortable: false,
            filterable: false,
            width: 100,
            maxwidth: 100,
            minwidth: 100
        }

     ]


     return(
      <div>
         <ReactTable 
          columns={columns}
          data={this.props.posts}
          pageSize={this.props.posts.length}
          showPagination={false}
          className="-striped -highlight"
          >
          </ReactTable>


      </div>
    )

  }

}


export default PostTable;
